document.addEventListener('DOMContentLoaded',function(){var a=document.querySelector('.hamburger-menu'),b=document.querySelector('.nav-content');a.addEventListener('click',function(){a.classList.toggle('active');b.classList.toggle('active')});for(const A of document.querySelectorAll('.nav-content a'))A.addEventListener('click',()=>{a.classList.remove('active');b.classList.remove('active')})});
